mapper.select:

SELECT * FROM `order` a WHERE user_id = #{userId} 
	AND merch_type < 2 


	AND state = #{state} 


	AND (SELECT COUNT(1) FROM `order` WHERE state = a.state AND a.id <= id) <= #{top} 
ORDER BY state DESC, creation desc


SELECT * FROM `order` a WHERE user_id = #{userId} 
	AND merch_type >= 2 


	AND state = #{state} 


	AND (SELECT COUNT(1) FROM `order` WHERE state = a.state AND a.id <= id) <= #{top} 
ORDER BY state DESC, creation desc

mapper.insert:

	select @@identity as id 

insert into `order`(
	id
	,no
	,amount
	,user_id
	,merch_id
	,merch_type
	,quantity
	,state

		,description

	,creation

		,updation

) values(
	#{id}
	,#{no}
	,#{amount}
	,#{userId}
	,#{merchandiseId}
	,#{merchandiseType}
	,#{quantity}
	,#{state}

		,#{description}

	,#{creation}

		,#{updation}

)
